// Extract device info and payload from ChirpStack v4 event
const deviceEui = msg.payload.deviceInfo?.devEui || 'unknown';
const deviceName = msg.payload.deviceInfo?.deviceName || 'unknown';

// Get decoded data (from codec) or raw base64
let data = msg.payload.object || {};
if (Object.keys(data).length === 0 && msg.payload.data) {
    data = { raw: msg.payload.data };
}

// Add metadata
data._rssi = msg.payload.rxInfo?.[0]?.rssi;
data._snr = msg.payload.rxInfo?.[0]?.snr;
data._fCnt = msg.payload.fCnt;
data._fPort = msg.payload.fPort;

// Prepare for PostgreSQL - params array for $1, $2
msg.params = [deviceEui, JSON.stringify(data)];
msg.deviceEui = deviceEui;
msg.deviceName = deviceName;
msg.sensorData = data;

node.status({fill: 'green', shape: 'dot', text: deviceEui.substring(8,16)});

return msg;

const data = msg.sensorData;
const alerts = [];

if (data.water_level !== undefined && data.water_level < 20) {
    alerts.push({ type: 'water_level_low', message: `Water level low: ${data.water_level}%`, device: msg.deviceEui });
}
if (data.temperature !== undefined && data.temperature > 35) {
    alerts.push({ type: 'temperature_high', message: `High temperature: ${data.temperature}Â°C`, device: msg.deviceEui });
}
if (data.battery !== undefined && data.battery < 20) {
    alerts.push({ type: 'battery_low', message: `Battery low: ${data.battery}%`, device: msg.deviceEui });
}

if (alerts.length > 0) {
    return { payload: alerts, deviceEui: msg.deviceEui };
}
return null;

// Test downlink message formatter for ChirpStack v4
// CRITICAL: devEui must be in the payload AND match the topic

// Configuration - CHANGE THESE:
const applicationId = "885c4dd2-e474-4265-bd25-2f838571f8de";  // Your ChirpStack application ID (check ChirpStack UI)
const devEui = "48ca43fffe3f0e70";  // Your device EUI (lowercase, no colons)
const port = 20;  // Test port
const testMessage = "Hello from Node-RED!";  // Your test message

// Convert message to bytes (UTF-8)
const messageBytes = Buffer.from(testMessage, 'utf8');

// Convert to base64 (ChirpStack expects base64-encoded data)
const dataBase64 = messageBytes.toString('base64');

// ChirpStack v4 downlink command format
// CRITICAL: devEui field MUST be present and match the topic
msg.payload = {
    devEui: devEui,        // REQUIRED: Must match topic DevEUI
    confirmed: false,       // Unconfirmed downlink (faster, no ACK required)
    fPort: port,           // Application port
    data: dataBase64       // Base64-encoded payload
};

// Set topic - application ID and DevEUI must match payload
msg.topic = `application/${applicationId}/device/${devEui}/command/down`;

return msg;

// Only pass through data for the currently selected device
const selectedDevice = flow.get('selectedDevice');
const incomingDevice = msg.deviceEui;

// Only pass if device matches
if (incomingDevice === selectedDevice) {
    return msg;
}

// Filter out other devices
return null;

const devices = msg.payload.map(row => ({
    eui: row.eui,
    name: `Device ${row.eui.substring(0, 8)}...`
}));

return [
    {
        topic: 'deviceList',
        payload: devices
    },
    {
        payload: devices[0].eui
    }
];

const deviceEui = msg.payload;

msg.params = [deviceEui];
msg.query = `
    SELECT
        ts,
        data
    FROM readings
    WHERE device_eui = $1
    ORDER BY ts DESC
    LIMIT 1
`;

flow.set('selectedDevice', deviceEui);
return msg;

const row = msg.payload[0];
if (!row) {
    return {
        topic: 'deviceData',
        payload: { current: {} }
    };
}

const data = row.data;

// Calculate uptime
let uptime = '--';
if (data.tsr) {
    const seconds = parseInt(data.tsr);
    const days = Math.floor(seconds / 86400);
    const hours = Math.floor((seconds % 86400) / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    uptime = `${days}d ${hours}h ${mins}m`;
}

return {
    topic: 'deviceData',
    payload: {
        current: {
            battery: parseFloat(data.bp) || 0,
            rssi: parseFloat(data._rssi) || -120,
            snr: parseFloat(data._snr) || -20,
            waterLevel: parseFloat(data.wl) || 0,
            waterVolume: parseFloat(data.tv) || 0,
            flowRate: parseFloat(data.fr) || 0,
            uptime: uptime
        }
    }
};

const result = msg.payload[0];

return {
    topic: 'chartData',
    payload: {
        battery: result.battery || [],
        water: result.water || [],
        rssi: result.rssi || [],
        snr: result.snr || []
    }
};

const data = msg.sensorData;

if (!data) {
    node.warn('No sensor data in message');
    return null;
}

// Calculate uptime from tsr (time since reset in seconds)
let uptime = '--';
if (data.tsr) {
    const seconds = parseInt(data.tsr);
    const days = Math.floor(seconds / 86400);
    const hours = Math.floor((seconds % 86400) / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    uptime = `${days}d ${hours}h ${mins}m`;
}

return {
    topic: 'realtimeUpdate',
    payload: {
        battery: parseFloat(data.bp) || 0,
        rssi: parseFloat(data._rssi) || -120,
        snr: parseFloat(data._snr) || -20,
        waterLevel: parseFloat(data.wl) || 0,
        waterVolume: parseFloat(data.tv) || 0,
        flowRate: parseFloat(data.fr) || 0,
        uptime: uptime
    }
};

if (msg.uibuilderCtrl === "client connect"){
    msg.topic = "connected"
    return msg
}
return null

// Water Tank Configuration
// This is the single source of truth for tank parameters

const config = {
    // Tank Physical Dimensions
    tank: {
        height_cm: 200,          // Total tank height in cm
        capacity_liters: 5000,   // Total tank capacity in liters
        diameter_cm: 180,        // Tank diameter for visualization
        shape: 'cylindrical'     // cylindrical, rectangular
    },

    // Distance Sensor Configuration
    sensor: {
        mounting_height_cm: 210,     // Height where sensor is mounted from ground
        offset_cm: 0,                 // Calibration offset (adjust after testing)
        min_reading_cm: 10,           // Minimum valid reading (sensor dead zone)
        max_reading_cm: 220,          // Maximum valid reading
        field_name: 'pd'              // Field name in data payload for distance
    },

    // Flow Calculation
    flow: {
        reporting_interval_seconds: 30,  // How often device reports
        flow_field_name: 'tv',           // Total volume field name
        enable_flow_calculation: true,   // Enable flow rate calculations
        flow_detection_threshold_liters: 5  // Minimum change to detect flow
    },

    // Alert Thresholds
    alerts: {
        low_level_percent: 20,
        critical_level_percent: 10,
        high_flow_rate_lph: 1000  // Liters per hour - potential leak
    }
};

// Store in global context
global.set('waterTankConfig', config);

// Return config for debugging
msg.payload = config;
node.status({fill: 'green', shape: 'dot', text: 'Config loaded'});

return msg;
