// Extract device info and fPort from ChirpStack v4 event
const deviceEui = msg.payload.deviceInfo?.devEui || 'unknown';
const deviceName = msg.payload.deviceInfo?.deviceName || '';
const fPort = msg.payload.fPort || 2; // Default to telemetry

// Get decoded data
let data = msg.payload.object || {};

// Add metadata
const rssi = msg.payload.rxInfo?.[0]?.rssi;
const snr = msg.payload.rxInfo?.[0]?.snr;

msg.deviceEui = deviceEui;
msg.deviceName = deviceName;
msg.fPort = fPort;
msg.data = data;
msg.rssi = rssi;
msg.snr = snr;
msg.rawPayload = msg.payload;

node.status({fill: 'green', shape: 'dot', text: `${deviceEui.slice(-4)} fPort:${fPort}`});

return msg;

// Parse registration message from device
const deviceEui = msg.deviceEui;
const reg = msg.data;

if (!reg || !reg.fields) {
    node.warn('Invalid registration: missing fields');
    return null;
}

// Store full registration
msg.registration = reg;

// Prepare device upsert
msg.deviceParams = [
    deviceEui,
    msg.deviceName || reg.type || 'Unknown',
    reg.type || 'unknown',
    reg.fw || '0.0.0',
    JSON.stringify(reg)
];

node.status({fill: 'blue', shape: 'dot', text: `Registered: ${deviceEui.slice(-4)}`});

return msg;

const deviceEui = msg.deviceEui;
const reg = msg.registration;

// Build batch inserts for fields, controls, triggers
const fields = reg.fields || [];
const triggers = reg.triggers || [];
const cmds = reg.cmds || [];

// Delete existing and re-insert (simpler than upsert for arrays)
msg.deleteParams = [deviceEui];

// Prepare field inserts
msg.fieldRows = fields.map(f => ({
    device_eui: deviceEui,
    field_key: f.k,
    display_name: f.n,
    data_type: f.t,
    unit: f.u || null,
    category: f.c,
    min_value: f.min ?? null,
    max_value: f.max ?? null,
    enum_values: f.v ? JSON.stringify(f.v) : null
}));

// Prepare control inserts (for state fields)
msg.controlRows = fields
    .filter(f => f.c === 'state')
    .map(f => ({
        device_eui: deviceEui,
        control_key: f.k,
        current_state: f.v ? f.v[0] : 'off',
        mode: 'auto'
    }));

// Prepare trigger inserts
msg.triggerRows = triggers.map(t => ({
    device_eui: deviceEui,
    trigger_key: t.k,
    display_name: t.n,
    field_key: t.f,
    operator: t.op,
    threshold: t.th,
    action_control: t.ctrl,
    action_state: t.st,
    enabled: true
}));

// Infer viz_config defaults
msg.vizRows = fields.map(f => {
    let vizType = 'badge';
    let gaugeStyle = null;
    let chartColor = '#3b82f6';
    let sortOrder = 100;

    if (f.c === 'state') {
        vizType = 'toggle';
    } else if (f.t === 'num' && f.c === 'cont') {
        vizType = (f.min !== undefined && f.max !== undefined) ? 'both' : 'chart';
        gaugeStyle = 'radial';

        // Special cases by field key
        const key = f.k.toLowerCase();
        if (key.includes('level') || key.includes('tank') || key === 'wl') {
            gaugeStyle = 'liquid';
            chartColor = '#3b82f6';
            sortOrder = 10;
        } else if (key.includes('battery') || key === 'bp') {
            chartColor = '#10b981';
            sortOrder = 20;
        } else if (key.includes('rssi')) {
            chartColor = '#f59e0b';
            sortOrder = 80;
        } else if (key.includes('snr')) {
            chartColor = '#8b5cf6';
            sortOrder = 90;
        }
    }

    // Default thresholds for bounded numeric
    let thresholds = null;
    if (f.t === 'num' && f.min !== undefined && f.max !== undefined) {
        thresholds = JSON.stringify([
            {pct: 0.2, color: '#ef4444'},
            {pct: 0.5, color: '#f59e0b'},
            {pct: 1.0, color: '#10b981'}
        ]);
    }

    return {
        device_eui: deviceEui,
        field_key: f.k,
        viz_type: vizType,
        gauge_style: gaugeStyle,
        chart_color: chartColor,
        thresholds: thresholds,
        is_visible: true,
        sort_order: sortOrder
    };
});

return msg;

query:
DELETE FROM device_fields WHERE device_eui = $1;
DELETE FROM device_controls WHERE device_eui = $1;
DELETE FROM device_triggers WHERE device_eui = $1;

