// Build INSERT for fields
if (msg.fieldRows.length === 0) return msg;

const values = msg.fieldRows.map((r, i) => {
    const offset = i * 9;
    return `($${offset+1}, $${offset+2}, $${offset+3}, $${offset+4}, $${offset+5}, $${offset+6}, $${offset+7}, $${offset+8}, $${offset+9}::jsonb)`;
}).join(',\n');

msg.query = `INSERT INTO device_fields 
(device_eui, field_key, display_name, data_type, unit, category, min_value, max_value, enum_values)
VALUES ${values}`;

msg.params = msg.fieldRows.flatMap(r => [
    r.device_eui, r.field_key, r.display_name, r.data_type,
    r.unit, r.category, r.min_value, r.max_value, r.enum_values
]);

return msg;

if (msg.controlRows.length === 0) return msg;

const values = msg.controlRows.map((r, i) => {
    const offset = i * 4;
    return `($${offset+1}, $${offset+2}, $${offset+3}, $${offset+4})`;
}).join(',\n');

msg.query = `INSERT INTO device_controls 
(device_eui, control_key, current_state, mode)
VALUES ${values}`;

msg.params = msg.controlRows.flatMap(r => [
    r.device_eui, r.control_key, r.current_state, r.mode
]);

return msg;

if (msg.triggerRows.length === 0) return msg;

const values = msg.triggerRows.map((r, i) => {
    const offset = i * 9;
    return `($${offset+1}, $${offset+2}, $${offset+3}, $${offset+4}, $${offset+5}, $${offset+6}, $${offset+7}, $${offset+8}, $${offset+9})`;
}).join(',\n');

msg.query = `INSERT INTO device_triggers 
(device_eui, trigger_key, display_name, field_key, operator, threshold, action_control, action_state, enabled)
VALUES ${values}`;

msg.params = msg.triggerRows.flatMap(r => [
    r.device_eui, r.trigger_key, r.display_name, r.field_key,
    r.operator, r.threshold, r.action_control, r.action_state, r.enabled
]);

return msg;

if (msg.vizRows.length === 0) return msg;

const values = msg.vizRows.map((r, i) => {
    const offset = i * 8;
    return `($${offset+1}, $${offset+2}, $${offset+3}, $${offset+4}, $${offset+5}, $${offset+6}::jsonb, $${offset+7}, $${offset+8})`;
}).join(',\n');

msg.query = `INSERT INTO viz_config 
(device_eui, field_key, viz_type, gauge_style, chart_color, thresholds, is_visible, sort_order)
VALUES ${values}
ON CONFLICT (device_eui, field_key) DO UPDATE SET
    viz_type = COALESCE(viz_config.viz_type, EXCLUDED.viz_type),
    gauge_style = COALESCE(viz_config.gauge_style, EXCLUDED.gauge_style),
    chart_color = COALESCE(viz_config.chart_color, EXCLUDED.chart_color)`;

msg.params = msg.vizRows.flatMap(r => [
    r.device_eui, r.field_key, r.viz_type, r.gauge_style,
    r.chart_color, r.thresholds, r.is_visible, r.sort_order
]);

return msg;

// Send registration notification to UI
return {
    topic: 'deviceRegistered',
    payload: {
        eui: msg.deviceEui,
        name: msg.deviceName,
        type: msg.registration.type,
        firmware: msg.registration.fw
    }
};

// Process telemetry data
const deviceEui = msg.deviceEui;
const data = msg.data;

// Prepare telemetry insert
msg.telemetryParams = [
    deviceEui,
    JSON.stringify(data),
    msg.rssi,
    msg.snr
];

// Update last_seen
msg.updateDeviceParams = [deviceEui];

node.status({fill: 'green', shape: 'dot', text: `Telemetry: ${deviceEui.slice(-4)}`});

return msg;

// Update device_controls with state values from telemetry
const deviceEui = msg.deviceEui;
const data = msg.data;

// Get state fields from context (or query later)
// For now, check common state fields
const stateFields = ['pump', 'valve'];
const updates = [];

for (const key of stateFields) {
    if (data[key] !== undefined) {
        updates.push({ control_key: key, state: data[key] });
    }
}

if (updates.length === 0) {
    // No state updates, proceed to push telemetry
    return [msg, null];
}

msg.stateUpdates = updates;
return [msg, msg];

const deviceEui = msg.deviceEui;
const updates = msg.stateUpdates;

// Build update query for each control
const cases = updates.map((u, i) => 
    `WHEN control_key = $${i*2+2} THEN $${i*2+3}`
).join(' ');

const keys = updates.map(u => u.control_key);

msg.query = `UPDATE device_controls 
SET current_state = CASE ${cases} END,
    last_change_at = NOW()
WHERE device_eui = $1 AND control_key IN (${keys.map((_, i) => `$${i*2+2}`).join(', ')})`;

msg.params = [deviceEui, ...updates.flatMap(u => [u.control_key, u.state])];

return msg;

return {
    topic: 'telemetry',
    payload: {
        eui: msg.deviceEui,
        data: msg.data,
        rssi: msg.rssi,
        snr: msg.snr,
        ts: new Date().toISOString()
    }
};

// Prepare query for user rules
msg.params = [msg.payload.eui];
msg.telemetryData = msg.payload.data;
msg.deviceEui = msg.payload.eui;
return msg;

const rules = msg.payload || [];
const data = msg.telemetryData;
const deviceEui = msg.deviceEui;

if (!rules.length || !data) return null;

// Operator functions
const ops = {
    '<': (a, b) => a < b,
    '>': (a, b) => a > b,
    '<=': (a, b) => a <= b,
    '>=': (a, b) => a >= b,
    '=': (a, b) => a == b,
    '==': (a, b) => a == b,
    '!=': (a, b) => a != b
};

const now = Date.now();
const triggeredRules = [];

for (const rule of rules) {
    // Check cooldown
    if (rule.last_triggered) {
        const elapsed = (now - new Date(rule.last_triggered).getTime()) / 1000;
        if (elapsed < (rule.cooldown_seconds || 300)) {
            continue;
        }
    }

    // Check if control is in manual mode
    if (rule.mode === 'manual') {
        if (!rule.manual_until || new Date(rule.manual_until) > new Date()) {
            continue; // Still in manual mode
        }
    }

    // Parse condition
    const condition = rule.condition;
    if (!condition || !condition.field || !condition.op) continue;

    const fieldValue = data[condition.field];
    if (fieldValue === undefined) continue;

    const op = ops[condition.op];
    if (!op) continue;

    // Evaluate condition
    if (op(fieldValue, condition.val)) {
        // Rule triggered!
        triggeredRules.push({
            ruleId: rule.id,
            ruleName: rule.name,
            control: rule.action_control,
            state: rule.action_state,
            reason: `rule:${rule.id}`
        });
    }
}

if (triggeredRules.length === 0) return null;

// For now, take first triggered rule (could add priority handling)
const triggered = triggeredRules[0];
msg.triggeredRule = triggered;
msg.ruleParams = [triggered.ruleId];

node.status({fill: 'yellow', shape: 'dot', text: `Rule: ${triggered.ruleName}`});

return msg;

const triggered = msg.triggeredRule;
const deviceEui = msg.deviceEui;

// Command ports by control type
const commandPorts = { 'pump': 20, 'valve': 21 };
const port = commandPorts[triggered.control] || 20;
const stateValue = (triggered.state === 'on' || triggered.state === 'open') ? 0x01 : 0x00;

// Prepare command log
msg.cmdParams = [
    deviceEui,
    `set_${triggered.control}`,
    JSON.stringify({ state: triggered.state }),
    triggered.reason,
    'sent'
];

// Build downlink
const appId = global.get('chirpstackAppId') || '885c4dd2-e474-4265-bd25-2f838571f8de';
msg.topic = `application/${appId}/device/${deviceEui}/command/down`;
msg.payload = {
    devEui: deviceEui,
    confirmed: false,
    fPort: port,
    data: Buffer.from([stateValue]).toString('base64')
};

return msg;

// Process state change event from device
const deviceEui = msg.deviceEui;
const data = msg.data;

const controlKey = data.control || data.ctrl;
const newState = data.state || data.st;
const reason = data.reason || 'device';
const deviceTs = data.ts ? new Date(data.ts * 1000) : null;

if (!controlKey || !newState) {
    node.warn('Invalid state change: missing control or state');
    return null;
}

msg.stateChange = {
    control_key: controlKey,
    new_state: newState,
    reason: reason,
    device_ts: deviceTs
};

msg.stateParams = [deviceEui, controlKey];

node.status({fill: 'yellow', shape: 'dot', text: `${controlKey}: ${newState}`});

return msg;

const oldState = msg.payload?.[0]?.current_state || null;
const sc = msg.stateChange;

msg.insertParams = [
    msg.deviceEui,
    sc.control_key,
    oldState,
    sc.new_state,
    sc.reason,
    sc.device_ts
];

msg.updateParams = [
    sc.new_state,
    sc.reason,
    msg.deviceEui,
    sc.control_key
];

return msg;

const sc = msg.stateChange;
return {
    topic: 'stateChange',
    payload: {
        eui: msg.deviceEui,
        control: sc.control_key,
        state: sc.new_state,
        reason: sc.reason
    }
};

// Process command acknowledgment from device
const deviceEui = msg.deviceEui;
const data = msg.data;

const cmdId = data.cmd_id || data.id;
const status = data.status || 'acked';

if (!cmdId) {
    node.warn('Invalid ACK: missing cmd_id');
    return null;
}

msg.ackParams = [status, cmdId, deviceEui];

node.status({fill: 'green', shape: 'dot', text: `ACK: ${cmdId}`});

return msg;

return {
    topic: 'commandAck',
    payload: {
        eui: msg.deviceEui,
        commandId: msg.ackParams[1],
        status: msg.ackParams[0]
    }
};

const devices = Array.isArray(msg.payload) ? msg.payload : [];
return {
    topic: 'devices',
    payload: devices
};

const eui = msg.payload?.eui;
if (!eui) return null;

msg.params = [eui];
flow.set('selectedDevice', eui);
flow.set('selectedRange', msg.payload?.range || '24h');

return msg;

msg.fields = msg.payload;
msg.params = [msg.params[0]];
return msg;

msg.controls = msg.payload;
msg.params = [msg.params[0]];
return msg;

msg.triggers = msg.payload;
msg.params = [msg.params[0]];
return msg;

const latest = msg.payload?.[0];

return {
    topic: 'deviceConfig',
    payload: {
        eui: msg.params[0],
        fields: msg.fields,
        controls: msg.controls,
        triggers: msg.triggers,
        current: latest ? {
            data: latest.data,
            rssi: latest.rssi,
            snr: latest.snr,
            ts: latest.ts
        } : null
    }
};

const eui = msg.payload?.eui || flow.get('selectedDevice');
const field = msg.payload?.field;
const range = msg.payload?.range || flow.get('selectedRange') || '24h';

if (!eui || !field) return null;

const intervals = {
    '1h': '1 hour',
    '6h': '6 hours',
    '24h': '24 hours',
    '7d': '7 days',
    '30d': '30 days'
};

const interval = intervals[range] || '24 hours';

msg.params = [eui, interval];
msg.field = field;
msg.query = `SELECT ts, (data->>'${field}')::numeric as value
FROM telemetry
WHERE device_eui = $1 AND ts > NOW() - $2::interval AND data->>'${field}' IS NOT NULL
ORDER BY ts`;

return msg;

return {
    topic: 'history',
    payload: {
        eui: msg.params[0],
        field: msg.field,
        data: msg.payload.map(r => ({ ts: r.ts, value: r.value }))
    }
};

const { eui, control, state, duration } = msg.payload;

if (!eui || !control || !state) return null;

// Calculate manual_until if duration specified
let manualUntil = null;
if (duration) {
    manualUntil = new Date(Date.now() + duration * 60 * 1000);
}

msg.params = [state, 'manual', manualUntil, 'user', eui, control];
msg.controlInfo = { eui, control, state };

return msg;

const { eui, control, state } = msg.controlInfo;

// Get command port from device registration
// For now, use defaults
const commandPorts = {
    'pump': 20,
    'valve': 21
};

const port = commandPorts[control] || 20;
const stateValue = (state === 'on' || state === 'open') ? 0x01 : 0x00;

// Log command
msg.cmdParams = [eui, `set_${control}`, JSON.stringify({state}), 'user', 'sent'];

// Build downlink
const appId = global.get('chirpstackAppId') || '885c4dd2-e474-4265-bd25-2f838571f8de';

msg.downlink = {
    topic: `application/${appId}/device/${eui}/command/down`,
    payload: {
        devEui: eui,
        confirmed: false,
        fPort: port,
        data: Buffer.from([stateValue]).toString('base64')
    }
};

return msg;

const { eui, control, state } = msg.controlInfo;
return {
    topic: 'stateChange',
    payload: {
        eui,
        control,
        state,
        reason: 'user'
    }
};

const { eui, control } = msg.payload;

if (!eui || !control) return null;

msg.params = ['auto', eui, control];
msg.controlInfo = { eui, control };

return msg;

const ctrl = msg.payload?.[0];
if (!ctrl) return null;

return {
    topic: 'controlUpdate',
    payload: {
        eui: msg.controlInfo.eui,
        control: ctrl.control_key,
        state: ctrl.current_state,
        mode: ctrl.mode
    }
};

if (msg.uibuilderCtrl === 'client connect') {
    return { topic: 'getDevices' };
}
return null;

const expired = msg.payload || [];
if (expired.length === 0) return null;

// Send control update for each expired override
const msgs = expired.map(c => ({
    topic: 'controlUpdate',
    payload: {
        eui: c.device_eui,
        control: c.control_key,
        state: c.current_state,
        mode: 'auto'
    }
}));

return [msgs];

const eui = msg.payload?.eui;
if (!eui) return null;
msg.params = [eui];
msg.deviceEui = eui;
return msg;

return {
    topic: 'rules',
    payload: {
        eui: msg.deviceEui,
        rules: msg.payload || []
    }
};

const rule = msg.payload;
if (!rule || !rule.eui) return null;

// If rule has id, it's an update
if (rule.id) {
    msg.query = `UPDATE user_rules SET
        name = $2, description = $3, condition = $4::jsonb,
        action_control = $5, action_state = $6, priority = $7,
        cooldown_seconds = $8, enabled = $9
    WHERE id = $1 AND device_eui = $10
    RETURNING *`;
    msg.params = [
        rule.id, rule.name, rule.description || null,
        JSON.stringify(rule.condition), rule.action_control, rule.action_state,
        rule.priority || 100, rule.cooldown_seconds || 300, rule.enabled ?? true,
        rule.eui
    ];
} else {
    msg.query = `INSERT INTO user_rules
        (device_eui, name, description, condition, action_control, action_state, priority, cooldown_seconds, enabled)
    VALUES ($1, $2, $3, $4::jsonb, $5, $6, $7, $8, $9)
    RETURNING *`;
    msg.params = [
        rule.eui, rule.name, rule.description || null,
        JSON.stringify(rule.condition), rule.action_control, rule.action_state,
        rule.priority || 100, rule.cooldown_seconds || 300, rule.enabled ?? true
    ];
}

msg.deviceEui = rule.eui;
return msg;

const saved = msg.payload?.[0];
return {
    topic: 'ruleSaved',
    payload: {
        eui: msg.deviceEui,
        rule: saved
    }
};

const { eui, ruleId } = msg.payload || {};
if (!eui || !ruleId) return null;
msg.params = [ruleId, eui];
msg.deviceEui = eui;
msg.ruleId = ruleId;
return msg;

return {
    topic: 'ruleDeleted',
    payload: {
        eui: msg.deviceEui,
        ruleId: msg.ruleId
    }
};

const { eui, triggerKey, enabled } = msg.payload || {};
if (!eui || !triggerKey || enabled === undefined) return null;
msg.params = [enabled, eui, triggerKey];
msg.deviceEui = eui;
msg.triggerKey = triggerKey;
return msg;

const saved = msg.payload?.[0];
return {
    topic: 'triggerSaved',
    payload: {
        eui: msg.deviceEui,
        trigger: saved
    }
};
