// Extract device info and payload from ChirpStack v4 event
const deviceEui = msg.payload.deviceInfo?.devEui || 'unknown';
const deviceName = msg.payload.deviceInfo?.deviceName || 'unknown';

// Get decoded data (from codec) or raw base64
let data = msg.payload.object || {};
if (Object.keys(data).length === 0 && msg.payload.data) {
    data = { raw: msg.payload.data };
}

// Add metadata
data._rssi = msg.payload.rxInfo?.[0]?.rssi;
data._snr = msg.payload.rxInfo?.[0]?.snr;
data._fCnt = msg.payload.fCnt;
data._fPort = msg.payload.fPort;

// Prepare for PostgreSQL - params array for $1, $2
msg.params = [deviceEui, JSON.stringify(data)];
msg.deviceEui = deviceEui;
msg.deviceName = deviceName;
msg.sensorData = data;

node.status({fill: 'green', shape: 'dot', text: deviceEui.substring(0,8) + '...'});

return msg;

const data = msg.sensorData;
const alerts = [];

if (data.water_level !== undefined && data.water_level < 20) {
    alerts.push({ type: 'water_level_low', message: `Water level low: ${data.water_level}%`, device: msg.deviceEui });
}
if (data.temperature !== undefined && data.temperature > 35) {
    alerts.push({ type: 'temperature_high', message: `High temperature: ${data.temperature}Â°C`, device: msg.deviceEui });
}
if (data.battery !== undefined && data.battery < 20) {
    alerts.push({ type: 'battery_low', message: `Battery low: ${data.battery}%`, device: msg.deviceEui });
}

if (alerts.length > 0) {
    return { payload: alerts, deviceEui: msg.deviceEui };
}
return null;

// Test downlink message formatter for ChirpStack v4
// CRITICAL: devEui must be in the payload AND match the topic

// Configuration - CHANGE THESE:
const applicationId = "885c4dd2-e474-4265-bd25-2f838571f8de";  // Your ChirpStack application ID (check ChirpStack UI)
const devEui = "48ca43fffe3f0e70";  // Your device EUI (lowercase, no colons)
const port = 20;  // Test port
const testMessage = "Hello from Node-RED!";  // Your test message

// Convert message to bytes (UTF-8)
const messageBytes = Buffer.from(testMessage, 'utf8');

// Convert to base64 (ChirpStack expects base64-encoded data)
const dataBase64 = messageBytes.toString('base64');

// ChirpStack v4 downlink command format
// CRITICAL: devEui field MUST be present and match the topic
msg.payload = {
    devEui: devEui,        // REQUIRED: Must match topic DevEUI
    confirmed: false,       // Unconfirmed downlink (faster, no ACK required)
    fPort: port,           // Application port
    data: dataBase64       // Base64-encoded payload
};

// Set topic - application ID and DevEUI must match payload
msg.topic = `application/${applicationId}/device/${devEui}/command/down`;

return msg;

// Extract all sensor data from the incoming message
const data = msg.sensorData || {};
const batteryPercent = data.bp;
const rssi = data._rssi;
const snr = data._snr;
const tsr = data.tsr;

// Format device name from EUI
const deviceEui = msg.deviceEui || 'unknown';
const deviceName = msg.deviceName || deviceEui;
const shortEui = deviceEui.substring(0, 8).toUpperCase();

// Create multiple outputs for different dashboard elements
return [
    // Output 1: Battery data for gauge
    batteryPercent !== undefined ? {
        payload: parseFloat(batteryPercent),
        topic: shortEui,
        deviceEui: deviceEui,
        deviceName: deviceName
    } : null,

    // Output 2: RSSI data for gauge
    rssi !== undefined ? {
        payload: parseFloat(rssi),
        topic: shortEui,
        deviceEui: deviceEui
    } : null,

    // Output 3: SNR data for gauge
    snr !== undefined ? {
        payload: parseFloat(snr),
        topic: shortEui,
        deviceEui: deviceEui
    } : null,

    // Output 4: Device info for display
    {
        payload: `${deviceName} (${shortEui})`,
        deviceEui: deviceEui,
        rssi: rssi,
        snr: snr,
        battery: batteryPercent
    },

    // Output 5: TSR data
    tsr !== undefined ? {
        payload: parseFloat(tsr),
        topic: shortEui
    } : null,

    // Output 6: Pass through complete data for water processor
    {
        sensorData: data,
        deviceEui: deviceEui,
        deviceName: deviceName
    },

    // Output 7: Battery for history chart
    batteryPercent !== undefined ? {
        payload: parseFloat(batteryPercent),
        topic: 'Battery'
    } : null,

    // Output 8: RSSI for history chart
    rssi !== undefined ? {
        payload: parseFloat(rssi),
        topic: 'RSSI'
    } : null,

    // Output 9: SNR for history chart  
    snr !== undefined ? {
        payload: parseFloat(snr),
        topic: 'SNR'
    } : null,

    // Output 10: RSSI for combined signal chart
    rssi !== undefined ? {
        payload: parseFloat(rssi),
        topic: 'RSSI'
    } : null,

    // Output 11: SNR for combined signal chart
    snr !== undefined ? {
        payload: parseFloat(snr),
        topic: 'SNR'
    } : null
];

// Get configuration from global context
const config = global.get('waterTankConfig');

if (!config) {
    node.warn('Water tank config not loaded yet');
    return null;
}

const data = msg.sensorData || {};
const deviceEui = msg.deviceEui || 'unknown';

// Extract sensor readings
const distanceReading = parseFloat(data[config.sensor.field_name]);
const totalVolume = parseFloat(data[config.flow.flow_field_name]);

// Initialize flow tracking in context
const flowContext = context.get('flowTracking') || {};
if (!flowContext[deviceEui]) {
    flowContext[deviceEui] = {
        lastVolume: totalVolume || 0,
        lastTimestamp: Date.now(),
        flowRate: 0
    };
}

const deviceFlow = flowContext[deviceEui];

// Calculate water level from distance sensor
let waterLevelCm = null;
let waterLevelPercent = null;
let waterVolumeLiters = null;

if (!isNaN(distanceReading) && 
    distanceReading >= config.sensor.min_reading_cm && 
    distanceReading <= config.sensor.max_reading_cm) {

    // Distance from sensor to water surface
    const adjustedDistance = distanceReading + config.sensor.offset_cm;

    // Water level = mounting height - distance to water - tank bottom offset
    // Assuming tank bottom is at ground level (adjust if needed)
    waterLevelCm = config.sensor.mounting_height_cm - adjustedDistance;

    // Ensure water level is within tank bounds
    waterLevelCm = Math.max(0, Math.min(waterLevelCm, config.tank.height_cm));

    // Calculate percentage
    waterLevelPercent = (waterLevelCm / config.tank.height_cm) * 100;

    // Calculate volume (simplified for cylindrical tank)
    if (config.tank.shape === 'cylindrical') {
        const fillRatio = waterLevelCm / config.tank.height_cm;
        waterVolumeLiters = config.tank.capacity_liters * fillRatio;
    }
}

// Calculate flow rate
let flowRateLPH = 0;  // Liters per hour
let volumeChangeLiters = 0;

if (!isNaN(totalVolume) && config.flow.enable_flow_calculation) {
    const now = Date.now();
    const timeDeltaSeconds = (now - deviceFlow.lastTimestamp) / 1000;

    if (timeDeltaSeconds > 0 && timeDeltaSeconds < 300) {  // Valid time window (< 5 min)
        volumeChangeLiters = totalVolume - deviceFlow.lastVolume;

        // Only calculate if change is significant
        if (Math.abs(volumeChangeLiters) >= config.flow.flow_detection_threshold_liters) {
            // Convert to liters per hour
            flowRateLPH = (volumeChangeLiters / timeDeltaSeconds) * 3600;

            // Update tracking
            deviceFlow.flowRate = flowRateLPH;
            deviceFlow.lastVolume = totalVolume;
            deviceFlow.lastTimestamp = now;
        }
    } else if (timeDeltaSeconds >= 300) {
        // Reset tracking after long gap
        deviceFlow.lastVolume = totalVolume;
        deviceFlow.lastTimestamp = now;
        deviceFlow.flowRate = 0;
    }
}

// Save context
flowContext[deviceEui] = deviceFlow;
context.set('flowTracking', flowContext);

// Prepare outputs
const shortEui = deviceEui.substring(0, 8).toUpperCase();

return [
    // Output 1: Water level percentage for gauge
    waterLevelPercent !== null ? {
        payload: waterLevelPercent,
        topic: shortEui,
        waterLevel_cm: waterLevelCm,
        waterVolume_liters: waterVolumeLiters
    } : null,

    // Output 2: Water volume in liters (text display)
    waterVolumeLiters !== null ? {
        payload: Math.round(waterVolumeLiters),
        topic: shortEui,
        capacity: config.tank.capacity_liters
    } : null,

    // Output 3: Flow rate for display
    {
        payload: Math.abs(flowRateLPH).toFixed(1),
        topic: shortEui,
        volumeChange: volumeChangeLiters,
        totalVolume: totalVolume
    },

    // Output 4: Total volume for history chart
    !isNaN(totalVolume) ? {
        payload: totalVolume,
        topic: shortEui,
        timestamp: Date.now()
    } : null
];

// Store water data points in context for charting
const historyKey = 'waterHistory_' + msg.topic;
let history = context.get(historyKey) || [];

// Add new data point
history.push({
    x: msg.timestamp || Date.now(),
    y: msg.payload
});

// Keep only last 24 hours (assuming 60s interval = 1440 points)
const maxPoints = 1440;
if (history.length > maxPoints) {
    history = history.slice(-maxPoints);
}

context.set(historyKey, history);

// Pass through for real-time chart update
return msg;

msg.payload = {
    status: 'ok',
    timestamp: new Date().toISOString(),
    service: 'farm-monitor'
};
return msg;

// Water Tank Configuration
// This is the single source of truth for tank parameters

const config = {
    // Tank Physical Dimensions
    tank: {
        height_cm: 200,          // Total tank height in cm
        capacity_liters: 5000,   // Total tank capacity in liters
        diameter_cm: 180,        // Tank diameter for visualization
        shape: 'cylindrical'     // cylindrical, rectangular
    },

    // Distance Sensor Configuration
    sensor: {
        mounting_height_cm: 210,     // Height where sensor is mounted from ground
        offset_cm: 0,                 // Calibration offset (adjust after testing)
        min_reading_cm: 10,           // Minimum valid reading (sensor dead zone)
        max_reading_cm: 220,          // Maximum valid reading
        field_name: 'pd'              // Field name in data payload for distance
    },

    // Flow Calculation
    flow: {
        reporting_interval_seconds: 60,  // How often device reports
        flow_field_name: 'tv',           // Total volume field name
        enable_flow_calculation: true,   // Enable flow rate calculations
        flow_detection_threshold_liters: 5  // Minimum change to detect flow
    },

    // Alert Thresholds
    alerts: {
        low_level_percent: 20,
        critical_level_percent: 10,
        high_flow_rate_lph: 1000  // Liters per hour - potential leak
    }
};

// Store in global context
global.set('waterTankConfig', config);

// Return config for debugging
msg.payload = config;
node.status({fill: 'green', shape: 'dot', text: 'Config loaded'});

return msg;
